SELECT pg_terminate_backend(pid) FROM pg_stat_activity WHERE datname = 'dishbrary';

grant usage on schema public to dishbrary;

DROP DATABASE if exists dishbrary;
--
-- Name: dishbrary; Type: DATABASE; Schema: -; Owner: znato
--

CREATE DATABASE dishbrary ENCODING = 'UTF8' LC_COLLATE = 'en_US.UTF-8' LC_CTYPE = 'en_US.UTF-8';

ALTER DATABASE dishbrary OWNER TO znato;

GRANT ALL PRIVILEGES ON DATABASE dishbrary TO dishbrary;

\connect dishbrary

create table category (id bigint, creation_date timestamp, modification_date timestamp, name varchar(255), primary key (id));
ALTER TABLE category OWNER TO dishbrary;
create sequence category_seq start with 1 increment by 1;

create table cuisine (id bigint, creation_date timestamp, modification_date timestamp, icon_file_name varchar(255), name varchar(255), primary key (id));
ALTER TABLE cuisine OWNER TO dishbrary;
create sequence cuisine_seq start with 1 increment by 1;

create table favourite_recipe (id bigint, creation_date timestamp, modification_date timestamp, recipe_id bigint, user_id bigint, primary key (id));
ALTER TABLE favourite_recipe OWNER TO dishbrary;
create sequence favourite_recipe_seq start with 1 increment by 1;
alter table favourite_recipe add constraint UK_FavRecipeUserAndRecipeId unique (user_id, recipe_id);

create table ingredient (id bigint, creation_date timestamp, modification_date timestamp, carbohydrate decimal(19,2), energykcal integer, fat decimal(19,2), image_file_name varchar(255), name varchar(255) not null unique , protein decimal(19,2), unit varchar(255), primary key (id));
ALTER TABLE ingredient OWNER TO dishbrary;
create sequence ingredient_seq start with 1 increment by 1;

create table recipe (id bigint, creation_date timestamp, modification_date timestamp, additional_info varchar(255), cook_time_in_millis bigint, cover_image_file_name varchar(255), instruction text, name varchar(255) not null, popularity_index bigint, portion integer, preparation_time_in_millis bigint, video_file_name varchar(255), owner_id bigint, primary key (id));
ALTER TABLE recipe OWNER TO dishbrary;
create sequence recipe_seq start with 1 increment by 1;

create table recipe_additional_images_file_names (recipe_id bigint not null, additional_images_file_names varchar(255));
ALTER TABLE recipe_additional_images_file_names OWNER TO dishbrary;

create table recipe_categories (recipe_id bigint not null, categories_id bigint not null, primary key(recipe_id, categories_id) );
ALTER TABLE recipe_categories OWNER TO dishbrary;

create table recipe_cuisines (recipe_id bigint not null, cuisines_id bigint not null, primary key(recipe_id, cuisines_id) );
ALTER TABLE recipe_cuisines OWNER TO dishbrary;

create table recipe_ingredient (id bigint, creation_date timestamp, modification_date timestamp, quantity integer not null, selected_unit varchar(255), ingredient_id bigint, recipe_id bigint, primary key (id, recipe_id, ingredient_id));
ALTER TABLE recipe_ingredient OWNER TO dishbrary;
create sequence recipe_ingredient_seq start with 1 increment by 1;

create table dishbrary_right (id bigint, creation_date timestamp, modification_date timestamp, name varchar(255) not null unique, primary key (id));
ALTER TABLE dishbrary_right OWNER TO dishbrary;
create sequence right_seq start with 13 increment by 1;

create table dishbrary_role (id bigint, creation_date timestamp, modification_date timestamp, name varchar(255) not null unique , primary key (id));
ALTER TABLE dishbrary_role OWNER TO dishbrary;
create sequence role_seq start with 3 increment by 1;

create table dishbrary_role_rights (roles_id bigint not null, rights_id bigint not null, primary key(roles_id, rights_id) );
ALTER TABLE dishbrary_role_rights OWNER TO dishbrary;

create table dishbrary_user (id bigint, creation_date timestamp, modification_date timestamp, banned boolean, email varchar(255) unique not null, expired boolean, first_name varchar(255), last_login_date date, last_name varchar(255), password varchar(255) not null, profile_image_file_name varchar(255), username varchar(255) unique not null, role_id bigint, primary key (id));
ALTER TABLE dishbrary_user OWNER TO dishbrary;
create sequence user_seq start with 1 increment by 1;

-- *************************************************************
alter table favourite_recipe add constraint FK_FavRecipeRecipe foreign key (recipe_id) references recipe;
alter table favourite_recipe add constraint FK_FavRecipeUser foreign key (user_id) references dishbrary_user;

alter table recipe add constraint FK_RecipeUser foreign key (owner_id) references dishbrary_user;

alter table recipe_additional_images_file_names add constraint FK_RecipeImagesRecipe foreign key (recipe_id) references recipe;

alter table recipe_categories add constraint FK_RecipeCatCategory foreign key (categories_id) references category;
alter table recipe_categories add constraint FK_RecipeCatRecipe foreign key (recipe_id) references recipe;

alter table recipe_cuisines add constraint FK_RecipeCuisineCuisine foreign key (cuisines_id) references cuisine;
alter table recipe_cuisines add constraint FK_RecipeCuisineRecipe foreign key (recipe_id) references recipe;

alter table recipe_ingredient add constraint FK_RecipeIngredientIngredient foreign key (ingredient_id) references ingredient;
alter table recipe_ingredient add constraint FK_RecipeIngredientRecipe foreign key (recipe_id) references recipe;

alter table dishbrary_role_rights add constraint FK_RoleRightsRight foreign key (rights_id) references dishbrary_right;
alter table dishbrary_role_rights add constraint FK_RoleRightsRole foreign key (roles_id) references dishbrary_role;

alter table dishbrary_user add constraint FK_UserRole foreign key (role_id) references dishbrary_role;

GRANT ALL PRIVILEGES ON ALL TABLES IN SCHEMA public TO dishbrary;
GRANT ALL PRIVILEGES ON ALL SEQUENCES IN SCHEMA public TO dishbrary;





